<?php// Функции форматирования результатов перевода$source = 'yandex';$message = 'Ошибка';$yandex_key = 'dict.1.1.20160819T080857Z.a21f9f5c92e0e7b9.ab24906e2b9b24a62bede201ca3067abadaf5752';$lang = 'ru-en';$ui = 'ru';$article_from_source = getArticleFromSource($source, $lang, $message, $yandex_key, $ui);//Тестовый вывод функцийecho 'Translated word: '.$message;echo '<br><br>';echo 'full output function: '.full_output($message, $article_from_source);echo '<br><br>';echo 'short detailed output function: '.short_output_detailed($message, $article_from_source);echo '<br><br>';echo 'shortest output function: '.shortest_output($message, $article_from_source);// Получение статьи из внешнего источникаfunction getArticleFromSource($source, $lang, $input_text, $key, $ui){    $url = sprintf('https://dictionary.yandex.net/api/v1/dicservice.json/lookup?key=%s&lang=%s&text=%s&ui=%s&flags=2', $key, $lang, $input_text, $ui);    $json_data = file_get_contents($url);    return $json_data;}// Определение языка вводимого словаfunction lang_def($message, $key){}// Несколько вариантов переводаfunction full_output($input, $article){    $data = json_decode($article);    for ($i = 0; $i<=4; $i++) {        $trans[$i] = $data->def[0]->tr[$i]->text;    }    $transfiltered = array_filter ($trans);    $reply = 'The word "'.$input.'" translates like: '.implode(', ', $transfiltered).'.';    return $reply;}// Один Вариант перевода подробный (с частью речи и синонимами, если есть)function short_output_detailed($input, $article){    $data = json_decode($article);    $trans = $data->def[0]->tr[0]->text;    $pos = $data->def[0]->tr[0]->pos;    $syn = $data->def[0]->tr[0]->syn[0]->text;    if (empty($syn))    {        $reply = $trans.' ('.$pos.').';    }    else    {        $syn_pos = $data->def[0]->tr[0]->syn[0]->pos;        $reply = $trans.' ('.$pos.'), синоним - '.$syn.'. ('.$syn_pos.').';    }    return $reply;}// Один Вариант перевода (самый короткий, просто перевод)function shortest_output($input, $article){    $data = json_decode($article);    $reply = $data->def[0]->tr[0]->text;    return $reply;}